import React from 'react';
import { Link } from 'react-router-dom';
import './Header.css';
import AuthContext from '../../../context/AuthContext';
import { AuthProvider } from '../../../context/AuthContext';


import useWindowSize from './useWindowSize';

import logo_min from '.../../static/images/Logo-min';
import logo from '../../../static/images/Logo';

const Header = ({ isAuthenticated, onLogout }) => {
    const { width } = useWindowSize(); // Получаем ширину окна

    return (
        <header>
            <section className="header-top">
                <ul>
                    <li>
                        <Link to="/">
                            <img className="logo_min" src={logo_min} alt="Логотип маленький" />
                            <img className="logo" src={logo} alt="Логотип" />
                        </Link>
                    </li>
                    <li><a href="tel:+78352201209">+7 (8352)-20-12-09</a></li>
                    <li><a href="https://t.me/deamotis"><i className="fab fa-telegram"></i></a></li>
                    <li>
                        <h1 className="header-title-min">“Мой Силант”</h1>
                    </li>
                    <li>
                        {isAuthenticated ? (
                            <a className="logout" href="#" onClick={onLogout}>Выйти</a>
                        ) : (
                            <Link to="/login">Авторизация</Link>
                        )}
                    </li>
                </ul>
            </section>
            <h1 className="header-title">Электронная сервисная книжка “Мой Силант”</h1>
        </header>
    );
}

export default Header;
